mapper.select:
select num, category, title, content, to_Char(writeday, 'yyyy/mm/dd') writeday, readcnt, userid, state, open, gCode
from board
where num = #{num}

mapper.update:
update board
set readCnt = readCnt +1
where num = #{num}

update board
set title = #{title}, content = #{content}, open = #{open}
where num = #{num}

mapper.select:
select gCode, gName
from goods
where gCategory = #{gCategory}

select gCode, gCategory, gName
from goods
where gCode = #{gCode}

mapper.update:
update board
set title = #{title}, content = #{content}
where num = #{num}

mapper.insert:
update answer
set answer = #{answer}
where boardNum = #{boardNum}

mapper.update:
update board
set state = '답변완료'
where num = #{num}
